import React from "react";
import "./TasksList.css"
import Task from "../Task/Task"
class TasksList extends React.Component {
    constructor() {
        super();
        // this.handleDone = this.handleDone.bind(this);
        // this.handleRemove = this.handleRemove.bind(this);
    }
    handleRemove(key) {
        console.log(key);
        // let updatedList = this.props.tasks.filter((obj) => { return (obj.key !== key) });
        // this.props.tasks = updatedList;
    }
    // handleDone(key) {
    //     console.log(key);
        // let updatedList = this.props.tasks.filter((obj) => { return (obj.key !== key) });
        // this.props.tasks = updatedList;
       
    // }


    render() {
        if (this.props.tasks && this.props.tasks.length > 0) {
            return (
                <ul>
                    {this.props.tasks.map((obj, index) =>
                        <Task title={obj.task} description={obj.description} key={index}
                            handleDone={this.props.handleDone} handleRemove={this.props.handleRemove} />)}
                </ul>
            )
        }
        return <div>Waiting for your Task ....</div>

    }

}

export default TasksList;
